const std = @import("std");

pub fn caseFoldUtf8Writer(writer: anytype, utf8: []const u8) !void {
    if (@import("builtin").mode == .Debug) {
        std.debug.assert(std.unicode.utf8ValidateSlice(utf8));
    }
    var it = Utf8Iterator{ .bytes = utf8, .i = 0 };
    while (it.next()) |v| {
        if (v.codepoint >= cutoff) {
            try writer.writeAll(v.slice);
            continue;
        }
        const stage1_index = v.codepoint >> 8;
        const stage1_val = stage1[stage1_index];
        if (stage1_val == 0) {
            try writer.writeAll(v.slice);
            continue;
        }
        const stage2_index = @as(usize, stage1_val) * 256 + (v.codepoint & 0xFF);
        const stage3_index = stage2[stage2_index];
        var utf8_buf: [4]u8 = undefined;
        if (stage3_index & 0x80 != 0) {
            const real_index = @as(usize, multiple_start) + (stage3_index ^ 0x80) * 3;
            var i: u2 = 0;
            while (i < 3) : (i += 1) {
                const mapping: u21 = @intCast(stage3[real_index + i]);
                if (mapping == 0) break;

                const utf8_len = std.unicode.utf8Encode(mapping, &utf8_buf) catch unreachable;
                try writer.writeAll(utf8_buf[0..utf8_len]);
            }
        } else {
            const offset = stage3[stage3_index];
            if (offset == 0) {
                try writer.writeAll(v.slice);
                continue;
            }
            const mapping: u21 = @intCast(@as(i32, v.codepoint) + offset);
            const utf8_len = std.unicode.utf8Encode(mapping, &utf8_buf) catch unreachable;
            try writer.writeAll(utf8_buf[0..utf8_len]);
        }
    }
}

test caseFoldUtf8Writer {
    var output_list = std.ArrayList(u8).init(std.testing.allocator);
    defer output_list.deinit();

    try caseFoldUtf8Writer(output_list.writer(), "aBc! \u{00DF} \u{1F80} \u{0130} \u{1E921} \u{FB04}");
    try std.testing.expectEqualStrings("abc! ss \u{1F00}\u{03B9} \u{0069}\u{0307} \u{1E943} ffl", output_list.items);
}

// Like std.unicode.Utf8Iterator, but returns both the slice and the codepoint together
const Utf8Iterator = struct {
    bytes: []const u8,
    i: usize,

    const Value = struct {
        slice: []const u8,
        codepoint: u21,
    };

    pub fn next(it: *Utf8Iterator) ?Value {
        if (it.i >= it.bytes.len) {
            return null;
        }

        const cp_len = std.unicode.utf8ByteSequenceLength(it.bytes[it.i]) catch unreachable;
        it.i += cp_len;
        const slice = it.bytes[it.i - cp_len .. it.i];
        return .{
            .slice = slice,
            .codepoint = std.unicode.utf8Decode(slice) catch unreachable,
        };
    }
};

